import React from 'react';
import './QRContainer.css';
import {IonInput, IonItem} from '@ionic/react';
import {QRCode} from 'react-qrcode-logo';

interface QRProps {
}

interface QRState {
  url: string;
}


class QRContainer extends React.Component<QRProps, QRState> {
  constructor(props: QRProps) {
    super(props);

    this.state = {
      url: "http://facebook.github.io/react/",
    };
  }

  handleInputChange(e: any) {
    this.setState({
      url: e.target.value
    });
  }


  render() {
    return (
      <div className="container">
        <IonItem>
          <IonInput value={this.state.url} placeholder="Enter Input" onIonChange={(e: any) => this.handleInputChange(e)}></IonInput>
        </IonItem>
        <QRCode 
        value={this.state.url}
        logoImage="assets/logo.png"
        logoWidth={40}
        logoOpacity={1}
        ecLevel='H'
        />
      </div>
    );
  }
}


export default QRContainer;
