import React, { useState } from 'react';
import './QRContainer.css';
import {IonInput, IonItem} from '@ionic/react';
import {QRCode} from 'react-qrcode-logo';

interface QRProps {
}

interface QRState {
  url: string;
}


const QRContainer: React.FC<> = (QRProps, QRState) => {
  const [ url, setURL ] = useState("http://facebook.github.io/react/");

  const handleInputChange =(e: any) => {
    this.setState({
      url: e.target.value
    });
  }



  return (
    <div className="container">
      <IonItem>
        <IonInput value={this.state.url} placeholder="Enter Input" onIonChange={(e: any) => this.handleInputChange(e)}></IonInput>
      </IonItem>
      <QRCode 
      value={this.state.url}
      logoImage="assets/logo.png"
      logoWidth={40}
      logoOpacity={1}
      ecLevel='H'
      />
    </div>
  );

}


export default QRContainer;
